mapper.update:
UPDATE member SET mEmail = #{mEmail} ,mName =#{mName},
		mPhone =#{mPhone},mAddr=#{mAddr},mPw=#{mPw}
		 WHERE mId = #{mId}

UPDATE product SET 
pName = #{pName}, pPrice = #{pPrice}, pCount = #{pCount}, 
pShip = #{pShip}, pMile = #{pMile}, pCate = #{pCate} 
WHERE pUrl = #{pUrl}

UPDATE product SET 
pCount = #{pCount} 
WHERE pName = #{pName}

mapper.insert:
insert into tbl_order(orderId, mId, jCatchName, userAddr1, userAddr2, userAddr3, jPhone, total_sum, jMemo, pNum)
    select #{orderId}, #{mId}, #{jCatchName}, #{userAddr1}, #{userAddr2}, #{userAddr3}, 0, cartStock, 0, pNum
    	from cart where pNum = #{pNum}

insert into tbl_order_details(orderDetailsNum, orderId, pNum, cartStock)
		    select nextval(tbl_order_details_seq), #{orderId}, pNum, cartStock
		        from cart where mId = #{mId}
